import React from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { Input, Button } from 'antd'
import { updateCurrentValue, updateWorkingList } from '../../modules/lists'
import './index.css'

class home2 extends React.Component {
  constructor() {
    super();

    this.state = {
      currentValue: '',
    };
 }
  searchForUser = () => {console.log(this.state.currentValue)}
    render() {
        return(
            <div>
                <Input 
                    // onChange={(e) => this.setState({currentValue: e.target.value})}
                    onChange={(e) => e.target.value === 13 ? this.searchForUser() : this.setState({currentValue: e.target.value})}
                    className='search-box'

                />
                <Button 
                    style={{width: 50}}
                    type='primary'
                    size='large'
                    onClick={() => updateWorkingList(this.state.currentValue)}
                />
            </div>
        )
    }
}

const mapStateToProps = ({ counter }) => ({
  })

  const mapDispatchToProps = dispatch =>
    bindActionCreators(
      {

        updateCurrentValue,
        updateWorkingList,
      },
      dispatch
    )

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(home2)
