    console.log('updateCurrentValue: ', val)
    return dispatch => {
        dispatch({
            type: UPDATE_CURRENT_VALUE,
            currentValue: val
        })
    }
}

export function updateWorkingList(val) {
    console.log('update working list: ', val)
    return dispatch => {
        dispatch({
            type: UPDATE_CURRENT_VALUE,
            currentValue: val
        })
    }
}
